com.mirth.connect.plugins.javascriptstep.script:
logger.warn("rawdata: " + connectorMessage.getRawData().toString());

var instanceId = msg['form']['instanceID'].toString();

globalMap.put('instanceId', instanceId);

if (globalMap.get('instance_id')){
     instance_id = globalMap.get('instance_id');
}

var DecedentDOB = " ";


var tempDOB = msg['recordTarget']['patientRole']['patient']['birthTime']['@value'].toString();

var bmonth = tempDOB.substr(4,2);
var bday = tempDOB.substr(6,2);
var byear = tempDOB.substr(0,4);


DecedentDOB = bmonth + "/"+bday +"/" + byear;

globalMap.put('DecedentDOB', DecedentDOB);

//DC Content


var doc = org.openhealthtools.mdht.uml.cda.util.CDAUtil.load(new java.io.ByteArrayInputStream(connectorMessage.getRawData().getBytes("UTF-8")));

logger.debug('Document ID: '+doc.getId().getExtension()); // get document ID


var structuredBodyComponents = doc.getComponent().getStructuredBody().getComponents();
var structuredBodyComponentsSize = structuredBodyComponents.size();
logger.debug('Size: ' + structuredBodyComponentsSize);

var sectionTitle = '';
var sectionCode = '';
var sectionValueSet = '';
var sectionCodeName = '';
var thisSection = null;

for (i = 0; i < structuredBodyComponentsSize; i++) {
	thisSection = structuredBodyComponents.get(i).getSection();
	//logger.debug('HELLO!');
	//sectionTitle = thisSection.getTitle().getText();

	sectionCode = thisSection.getCode().getCode();
	//logger.debug('Code: ');
	sectionCodeName = thisSection.getCode().getDisplayName();
	sectionValueSet = thisSection.getCode().getCodeSystemName();
	logger.debug('Found Section... '+sectionTitle+' ('+sectionValueSet+ ' Code: '+sectionCode+' ['+sectionCodeName+']');


/*
    if (sectionCode == '69409-1'){

    	   logger.debug('Death Cert!');
    }
*/




}

